"use client";

import { AreaChart as TremorAreaChart } from "@tremor/react";

export interface AreaChartProps {
  data: Array<Record<string, any>>;
  index: string;
  categories: string[];
  /**
   * Color scheme for the chart areas.
   * Uses semantic color names that map to your design system.
   * @default ['primary', 'secondary', 'accent']
   */
  colors?: string[];
  valueFormatter?: (value: number) => string;
  className?: string;
  showLegend?: boolean;
  showGridLines?: boolean;
  showXAxis?: boolean;
  showYAxis?: boolean;
  /**
   * Gradient display type
   * @default 'default'
   */
  showGradient?: boolean;
  connectNulls?: boolean;
}

export function AreaChart({
  data,
  index,
  categories,
  colors = ['primary', 'secondary', 'accent'],
  valueFormatter,
  className,
  showLegend = true,
  showGridLines = true,
  showXAxis = true,
  showYAxis = true,
  showGradient = true,
  connectNulls = false,
}: AreaChartProps) {
  return (
    <TremorAreaChart
      data={data}
      index={index}
      categories={categories}
      colors={colors}
      valueFormatter={valueFormatter}
      className={className}
      showLegend={showLegend}
      showGridLines={showGridLines}
      showXAxis={showXAxis}
      showYAxis={showYAxis}
      showGradient={showGradient}
      connectNulls={connectNulls}
    />
  );
}

