import { describe, it, expect } from 'vitest';
import { render, screen } from '@testing-library/react';
import { AreaChart } from './area-chart';

describe('AreaChart', () => {
  const mockData = [
    { month: 'Jan', value: 100 },
    { month: 'Feb', value: 200 },
  ];

  it('renders without crashing', () => {
    render(
      <AreaChart
        data={mockData}
        index="month"
        categories={['value']}
      />
    );
  });

  it('accepts custom className', () => {
    const { container } = render(
      <AreaChart
        data={mockData}
        index="month"
        categories={['value']}
        className="custom-class"
      />
    );
    expect(container.firstChild).toHaveClass('custom-class');
  });

  it('supports gradient toggle', () => {
    render(
      <AreaChart
        data={mockData}
        index="month"
        categories={['value']}
        showGradient={false}
      />
    );
  });
});
