"use client";

import { BarChart as TremorBarChart } from "@tremor/react";

export interface BarChartProps {
  data: Array<Record<string, any>>;
  index: string;
  categories: string[];
  /**
   * Color scheme for the chart bars.
   * Uses semantic color names that map to your design system.
   * @default ['primary', 'secondary', 'accent']
   */
  colors?: string[];
  valueFormatter?: (value: number) => string;
  className?: string;
  showLegend?: boolean;
  showGridLines?: boolean;
  showXAxis?: boolean;
  showYAxis?: boolean;
  layout?: 'vertical' | 'horizontal';
  stack?: boolean;
}

export function BarChart({
  data,
  index,
  categories,
  colors = ['primary', 'secondary', 'accent'],
  valueFormatter,
  className,
  showLegend = true,
  showGridLines = true,
  showXAxis = true,
  showYAxis = true,
  layout = 'vertical',
  stack = false,
}: BarChartProps) {
  return (
    <TremorBarChart
      data={data}
      index={index}
      categories={categories}
      colors={colors}
      valueFormatter={valueFormatter}
      className={className}
      showLegend={showLegend}
      showGridLines={showGridLines}
      showXAxis={showXAxis}
      showYAxis={showYAxis}
      layout={layout}
      stack={stack}
    />
  );
}

