component:
"use client";

import { DonutChart as TremorDonutChart } from "@tremor/react";

export interface DonutChartProps {
  data: Array<{
    name: string;
    value: number;
    [key: string]: any;
  }>;
  category: string;
  index: string;
  /**
   * Color scheme for the chart segments.
   * Uses semantic color names that map to your design system.
   * @default ['primary', 'secondary', 'accent', 'success', 'warning']
   */
  colors?: string[];
  valueFormatter?: (value: number) => string;
  className?: string;
  showLabel?: boolean;
  showAnimation?: boolean;
  variant?: 'donut' | 'pie';
}

export function DonutChart({
  data,
  category,
  index,
  colors = ['primary', 'secondary', 'accent', 'success', 'warning'],
  valueFormatter,
  className,
  showLabel = true,
  showAnimation = true,
  variant = 'donut',
}: DonutChartProps) {
  return (
    <TremorDonutChart
      data={data}
      category={category}
      index={index}
      colors={colors}
      valueFormatter={valueFormatter}
      className={className}
      showLabel={showLabel}
      showAnimation={showAnimation}
      variant={variant}
    />
  );
}

test:
import { describe, it, expect } from 'vitest';
import { render, screen } from '@testing-library/react';
import { DonutChart } from './donut-chart';

describe('DonutChart', () => {
  const mockData = [
    { name: 'A', value: 100 },
    { name: 'B', value: 200 },
  ];

  it('renders without crashing', () => {
    render(
      <DonutChart
        data={mockData}
        category="value"
        index="name"
      />
    );
  });

  it('accepts custom className', () => {
    const { container } = render(
      <DonutChart
        data={mockData}
        category="value"
        index="name"
        className="custom-class"
      />
    );
    expect(container.firstChild).toHaveClass('custom-class');
  });

  it('supports pie variant', () => {
    render(
      <DonutChart
        data={mockData}
        category="value"
        index="name"
        variant="pie"
      />
    );
  });
});
