component:
"use client";

import { cn } from "@ui/components";

export interface KpiCardProps {
  title: string;
  value: string | number;
  change?: string;
  changeType?: 'increase' | 'decrease' | 'neutral';
  description?: string;
  className?: string;
}

/**
 * Maps change types to semantic design token classes
 * Uses CSS custom properties for theme-aware colors
 */
const changeTypeStyles = {
  increase: "text-[var(--color-success)]",
  decrease: "text-[var(--color-error)]",
  neutral: "text-[var(--color-foreground-muted)]",
};

export function KpiCard({
  title,
  value,
  change,
  changeType = 'neutral',
  description,
  className,
}: KpiCardProps) {
  return (
    <div
      className={cn(
        "rounded-lg border border-[var(--color-border)] bg-[var(--color-background)] p-6 shadow-sm",
        className
      )}
    >
      <div className="space-y-2">
        <p className="text-sm text-[var(--color-foreground-muted)]">{title}</p>
        <div className="flex items-baseline justify-between">
          <p className="text-3xl font-semibold text-[var(--color-foreground)]">{value}</p>
          {change && (
            <span
              className={cn(
                "text-sm font-medium",
                changeTypeStyles[changeType]
              )}
            >
              {change}
            </span>
          )}
        </div>
        {description && (
          <p className="text-sm text-[var(--color-foreground-muted)]">{description}</p>
        )}
      </div>
    </div>
  );
}

test:
import { describe, it, expect } from 'vitest';
import { render, screen } from '@testing-library/react';
import { KpiCard } from './kpi-card';

describe('KpiCard', () => {
  it('renders title and value', () => {
    render(<KpiCard title="Test Title" value="123" />);

    expect(screen.getByText('Test Title')).toBeInTheDocument();
    expect(screen.getByText('123')).toBeInTheDocument();
  });

  it('displays change indicator when provided', () => {
    render(
      <KpiCard
        title="Metric"
        value="100"
        change="+10%"
        changeType="increase"
      />
    );

    expect(screen.getByText('+10%')).toBeInTheDocument();
  });

  it('shows description when provided', () => {
    render(
      <KpiCard
        title="Metric"
        value="100"
        description="vs. last month"
      />
    );

    expect(screen.getByText('vs. last month')).toBeInTheDocument();
  });

  it('applies custom className', () => {
    const { container } = render(
      <KpiCard
        title="Metric"
        value="100"
        className="custom-class"
      />
    );

    expect(container.firstChild).toHaveClass('custom-class');
  });
});
