"use client";

import { LineChart as TremorLineChart } from "@tremor/react";

export interface LineChartProps {
  data: Array<Record<string, any>>;
  index: string;
  categories: string[];
  /**
   * Color scheme for the chart lines.
   * Uses semantic color names that map to your design system.
   * @default ['primary', 'secondary', 'accent']
   */
  colors?: string[];
  valueFormatter?: (value: number) => string;
  className?: string;
  showLegend?: boolean;
  showGridLines?: boolean;
  showXAxis?: boolean;
  showYAxis?: boolean;
}

export function LineChart({
  data,
  index,
  categories,
  colors = ['primary', 'secondary', 'accent'],
  valueFormatter,
  className,
  showLegend = true,
  showGridLines = true,
  showXAxis = true,
  showYAxis = true,
}: LineChartProps) {
  return (
    <TremorLineChart
      data={data}
      index={index}
      categories={categories}
      colors={colors}
      valueFormatter={valueFormatter}
      className={className}
      showLegend={showLegend}
      showGridLines={showGridLines}
      showXAxis={showXAxis}
      showYAxis={showYAxis}
    />
  );
}

